unconstrained fn test_partial_match() {
    let mut Engine = DebugRandomEngine { seed: 0 };
    let mut foo: [u8; 1024] = Engine.get_random_bytes();
    let mut bar: [u8; 128] = [0; 128];
    for i in 0..128 {
        bar[i] = foo[i + 123];
    }
    let position = utils::search(foo, bar.as_slice(), 1024, 128);

    assert(position == 123);
}

